import React, { useEffect, useState } from 'react';

function VotesList() {
  const [votes, setVotes] = useState([]);

  useEffect(() => {
    fetch('http://localhost:3002/api/allVotes')
      .then((response) => response.json())
      .then((data) => {
        setVotes(data.data);
      })
      .catch((error) => console.error(error));
  }, []);

  // Función para formatear la fecha como día y mes
  const formatDate = (dateString) => {
    const options = { day: 'numeric', month: 'short' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  return (
    <div className="table-container-categoria list2">
      <h2>Detalles de los votos</h2>

      <table>
        <thead>
          <tr>
            <th>ID del Voto</th>
            <th>Candidato o Votante</th>
            <th>Es Candidato</th>
            <th>Fecha</th>
          </tr>
        </thead>
        <tbody>
          {votes.map((vote) => (
            <tr key={vote.id}>
              <td>{vote.id}</td>
              <td>{vote.is_candidate === 1 ? 'Candidato' : 'Votante'}</td>
              <td>{vote.is_candidate === 1 ? 'Sí' : 'No'}</td>
              <td>{formatDate(vote.date)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default VotesList;
