import '../css/CandidatesList.css'

import React, { useEffect, useState } from 'react';

function AddVoter() {

  const [formData, setFormData] = useState({
    documento: '',
    nombre: '',
    apellido: '',
    esCandidato: '0',
  });

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      const response = await fetch('http://localhost:3002/api/allCandidates', {
        method: 'POST',
        headers: {
          'Content-Type': 'application/json',
        },
        body: JSON.stringify(formData),
      });

      if (response.ok) {
        console.log('Usuario agregado correctamente.');
        // Puedes redirigir al usuario a otra página o mostrar un mensaje de éxito aquí.
      } else {
        console.error('Error al agregar usuario.');
        // Puedes mostrar un mensaje de error al usuario aquí.
      }
    } catch (error) {
      console.error('Error en la solicitud:', error);
      // Puedes mostrar un mensaje de error al usuario aquí.
    }
  };

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({ ...formData, [name]: value });
  };


return (
    <div className="table-container-categoria list3">
      <div class="dataList">
      <h2>Agregar usuario</h2>
      </div>

    <form id="addVoter">

    <label for="documento">Documento:</label>
    <input type="text" id="documento" name="documento" required></input>

    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" name="nombre" required></input>

    <label for="apellido">Apellido:</label>
    <input type="text" id="apellido" name="apellido" required></input>

    <label for="esCandidato">¿Es Candidato?</label>
    <div className='radio-container'>
    <input type="radio" id="esCandidatoSi" name="esCandidato" value="1"></input>
    <label for="esCandidatoSi">Sí</label>

    <input type="radio" id="esCandidatoNo" name="esCandidato" value="0" checked></input>
    <label for="esCandidatoNo">No</label>
    </div>

    <button type="submit">Agregar Usuario</button>

  </form>


    </div>
);
}

export default AddVoter;
