import React, { useEffect, useState } from 'react';

function ListadoProductos() {
    const [usuarios, setUsuarios] = useState([]);
    const [productos, setProductos] = useState([]); // Asegúrate de inicializar productos como un array vacío

    useEffect(() => {
      // Realiza las solicitudes a ambas API en paralelo utilizando Promise.all
      Promise.all([
        fetch('https://tienda-sound.onrender.com/users/api/todoslosusuarios').then((response) => response.json()),
        fetch('https://tienda-sound.onrender.com/productos/api/todoslosproductos').then((response) => response.json())
      ])
      .then(([usuariosData, productosData]) => {
        setUsuarios(usuariosData);
        setProductos(productosData.data); // Actualiza productosData.data para asignar solo el array de productos
      })
      .catch((error) => {
        console.error('Error al obtener datos', error);
      });
    }, []);

    // Función para obtener el email de un usuario por su ID
    const obtenerEmailPorId = (usuarioId) => {
      const usuarioEncontrado = usuarios.find((usuario) => usuario.id === usuarioId);
      return usuarioEncontrado ? usuarioEncontrado.email : 'Usuario no encontrado';
    };

  return (
    <div>
      <h2>Productos</h2>
      <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Envío</th>
            <th>Estado</th>
            <th>Categoría</th>
            <th>Creado Por</th>
          </tr>
        </thead>
        <tbody>
          {productos.map((producto) => (
            <tr key={producto.id}>
              <td>{producto.id}</td>
              <td>{producto.nombreProducto}</td>
              <td>${producto.precioProducto}</td>
              <td>{producto.envio ? 'Sí' : 'No'}</td>
              <td>{producto.estadoProducto}</td>
              <td>{producto.categoria_id}</td>
              <td>{obtenerEmailPorId(producto.usuario_id)}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default ListadoProductos;
