import '../css/CandidatesList.css'

import React, { useEffect, useState } from 'react';

function AddVoter() {

  const [formData, setFormData] = useState({
    documento: '',
    nombre: '',
    apellido: '',
    esCandidato: '0', // Valor por defecto para "No"
  });

  const handleInputChange = (event) => {
    const { name, value } = event.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  const handleSubmit = (event) => {
    event.preventDefault();

    // Enviar datos al servidor
    fetch('http://localhost:3002/api/allCandidates', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify(formData),
    })
      .then(response => response.json())
      .then(data => {
        console.log(data); // Puedes manejar la respuesta del servidor aquí
        // Puedes realizar acciones adicionales según la respuesta del servidor
      })
      .catch(error => console.error(error));
  };



return (
    <div className="table-container-categoria list3">
      <div class="dataList">
      <h2>Agregar usuario</h2>
      </div>

    <form id="addVoter">

    <label for="documento">Documento:</label>
    <input type="text" id="documento" name="documento" required></input>

    <label for="nombre">Nombre:</label>
    <input type="text" id="nombre" name="nombre" required></input>

    <label for="apellido">Apellido:</label>
    <input type="text" id="apellido" name="apellido" required></input>

    <label for="esCandidato">¿Es Candidato?</label>
    <div className='radio-container'>
    <input type="radio" id="esCandidatoSi" name="esCandidato" value="1"></input>
    <label for="esCandidatoSi">Sí</label>

    <input type="radio" id="esCandidatoNo" name="esCandidato" value="0" checked></input>
    <label for="esCandidatoNo">No</label>
    </div>

    <button type="submit">Agregar Usuario</button>

  </form>


    </div>
);
}

export default AddVoter;
