import '../css/cards.css'
import React, { useState } from 'react';
import { Redirect } from 'react-router-dom';


function Form() {
  // ... (estado y funciones del formulario)

  const [isLoggedIn, setIsLoggedIn] = useState(false); // Nuevo estado para controlar la redirección

  const handleSubmit = async (event) => {
    event.preventDefault();

    try {
      // ... (código para enviar la solicitud a la API y verificar las credenciales)

      if (response.ok) {
        console.log('Inicio de sesión exitoso');
        setIsLoggedIn(true); // Actualiza el estado a true si el inicio de sesión es exitoso
      } else {
        console.log('Credenciales incorrectas');
        // Mostrar mensaje de error al usuario
      }
    } catch (error) {
      console.error('Error al iniciar sesión:', error);
    }
  };

  // Si el usuario está autenticado, redirigir al dashboard
  if (isLoggedIn) {
    return <Redirect to="/dashboard" />;
  }

  return (
    <div className="login-form">
      <h2>Iniciar Sesión</h2>
      <form onSubmit={handleSubmit}>
        <div className="form-group">
          <label htmlFor="username">Usuario:</label>
          <input
            type="text"
            id="username"
            name="username"
            value={username}
            onChange={(e) => setUsername(e.target.value)}
            required
          />
        </div>
        <div className="form-group">
          <label htmlFor="password">Contraseña:</label>
          <input
            type="password"
            id="password"
            name="password"
            value={password}
            onChange={(e) => setPassword(e.target.value)}
            required
          />
        </div>
        <button type="submit">Iniciar Sesión</button>
      </form>
    </div>
  );
}



export default Form;
