import '../css/cards.css'
import React, { useEffect, useState } from 'react';


function CardUltimoUsuario(){

  const [total, setTotal] = useState(0);
  const [usuarioConMayorId, setUsuarioConMayorId] = useState(null);

    useEffect(() => {

    fetch('https://tienda-sound.onrender.com/users/api/todoslosusuarios')
      .then((response) => response.json())
      .then((resultado) => {

        const totalUsuarios = resultado.total;
        setTotal(totalUsuarios);

         // Obtén un array de todos los IDs de usuarios
         const idsUsuarios = resultado.users.map(usuario => usuario.id);

         // Encuentra el ID máximo usando Math.max()
         const idMayor = Math.max(...idsUsuarios);

         // Encuentra el usuario con el ID máximo
         const usuarioConMayorId = resultado.users.find(usuario => usuario.id === idMayor);

         setUsuarioConMayorId(usuarioConMayorId);






      })
      .catch((error) => console.error(error));
  }, []);


    return(
        <div className='cards'>
            <p>Último usuario agregado</p>
            {usuarioConMayorId && (
              <div>
              <h3>Usuario con ID Mayor:</h3>
              <img src="https://res.cloudinary.com/dlf8flk1o/image/upload/v1693692976/avatars/{usuarioConMayorId.imagen}" alt=""></img>
              <p>{usuarioConMayorId.nombre}</p>
              <p>{usuarioConMayorId.email}</p>
            </div>
      )}


            <h2>{total}</h2>
        </div>

    );

}



export default CardUltimoUsuario;
