import React, { useEffect, useState } from 'react';


function ListadoProductos(){

    const [total, setTotal] = useState(0);

    useEffect(() => {

    // Realizar la solicitud a la API para obtener la lista de todos los productos
    fetch('https://tienda-sound.onrender.com/productos/api/todoslosproductos')
      .then((response) => response.json())
      .then((resultado) => {
        // Obten el número total de productos
        const totalProductos = resultado.total;
        setTotal(totalProductos);
      })
      .catch((error) => console.error(error));
  }, []);


  return (
    <div>
      <h2>Productos</h2>
      <table border="1">
        <thead>
          <tr>
            <th>ID</th>
            <th>Nombre</th>
            <th>Precio</th>
            <th>Envío</th>
            <th>Estado</th>
            <th>Categoría</th>
            <th>Creado Por</th>
          </tr>
        </thead>
        <tbody>
          {totalProductos.map((producto) => (
            <tr key={producto.id}>
              <td>{producto.id}</td>
              <td>{producto.nombreProducto}</td>
              <td>${producto.precioProducto}</td>
              <td>{producto.envio ? 'Sí' : 'No'}</td>
              <td>{producto.estadoProducto}</td>
              <td>{producto.categoria_id}</td>
              <td>{producto.usuario_id}</td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );

}



export default ListadoProductos;
